import React, { useState } from 'react';
import Star from '../star';

const StarWrapper = ({ starCount }) => {

  const range = (number) => 
        Array.from(new Array(number), (x, i) => i)

  const [clickedID, setClickedID] = useState(0);
  const [hoveredID, setHoveredID] = useState(0);

  return (
    <div>
      {range(starCount).map((starID, index) => {
        console.log(starID,'starID');
        console.log(index,'index');
        console.log(clickedID,'clickedID')
        console.log(hoveredID,'hoveredID')



        return (
          <Star
            key={index}
            isLit={clickedID ? index : ''}
            onClick={() => setClickedID(index)}
            onHoverIn={()=>setHoveredID(index)}
            onHoverOut={()=>setHoveredID(0)}

          />
        )
      })}
    </div>
  )
}

export default StarWrapper;
